<?php\n// Set the URL of the protected content\n$protected_url = 'http://example.com/protected/content.php';\n\n// Set the username and password for the protected directory\n$username = 'your_username';\n$password = 'your_password';\n\n// Use curl to fetch the protected content\n$ch = curl_init($protected_url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_USERPWD, $username . ':' . $password);\n$content = curl_exec($ch);\ncurl_close($ch);\n\n// Output the protected content\necho $content;