$searchTerm = 'milwakee';\n$results = array();\n\n$query = mysql_query("SELECT city FROM $table WHERE city LIKE '%$searchTerm%'");\nwhile ($row = mysql_fetch_assoc($query)) {\n    $city = $row['city'];\n    $distance = levenshtein($searchTerm, $city);\n    $results[] = array('city' => $city, 'distance' => $distance);\n}\n\n// Sort results by distance\nusort($results, function($a, $b) {\n    return $a['distance'] - $b['distance'];\n});\n\n// Display top results\nforeach ($results as $result) {\n    echo $result['city'] . ' (Distance: ' . $result['distance'] . ')<br>';\n}