function transliterate($text) {\n    $map = array(\n        'á' => 'a', 'é' => 'e', 'í' => 'i', 'ó' => 'o', 'ú' => 'u',\n        'à' => 'a', 'è' => 'e', 'ì' => 'i', 'ò' => 'o', 'ù' => 'u',\n        'â' => 'a', 'ê' => 'e', 'î' => 'i', 'ô' => 'o', 'û' => 'u',\n        'ä' => 'a', 'ë' => 'e', 'ï' => 'i', 'ö' => 'o', 'ü' => 'u',\n        'ñ' => 'n', 'ç' => 'c',\n    );\n\n    return strtr($text, $map);\n}\n\n$text = 'áéíóúàèìòùâêîôûäëïöüñç';\necho transliterate($text); // outputs: aeiouaeiouaeiouaeiounc