$inputFile = 'file.csv';\n$tableName = 'file_csv';\n\n$fh = fopen($inputFile, 'r');\n$contents = fread($fh, 5120); // 5KB\nfclose($fh);\n\n$fileLines = explode("\n", $contents); // explode to make sure we are only using the first line.\n\n$fieldList = explode(',', $fileLines[0]); // separate columns, put into array\n\necho 'CREATE TABLE IF NOT EXISTS `'.$tableName.'` ('."<br/>\n";\n\nforeach ($fieldList as $i => $field) {\n    $field = trim($field); // remove any whitespace\n\n    if (is_numeric($field) && strpos($field, '.') === false) {\n        $typeInfo = 'int(11)';\n    } elseif (is_numeric($field) && strpos($field, '.') !== false) {\n        $typeInfo = 'float';\n    } else {\n        $typeInfo = 'varchar(80)';\n    }\n\n    echo "\t".'`'.$i.'` '.$typeInfo.' NOT NULL, --'.$field."<br/>\n";\n}\n\necho '  PRIMARY KEY  (`0`)'."<br/>\n";\necho ') ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=1 ;';