$ids = array(123, 535, 345, 567, 878);\n$count = count($ids);\n\n// Create a string of ? placeholders, separated by commas\n$placeholders = implode(',', array_fill(0, $count, '?'));\n\n// Prepare the statement with the placeholders\n$stmt = $mysqli->prepare("SELECT foo, blar FROM table WHERE id IN ($placeholders)");\n\n// Bind each ID to its corresponding placeholder\nforeach ($ids as $i => $id) {\n    $stmt->bind_param('i', $id);\n}\n\n$stmt->execute();