function check_parentheses($str) {\n    $level = 0;\n    for ($i = 0; $i < strlen($str); $i++) {\n        if ($str[$i] == '(') {\n            $level++;\n        } elseif ($str[$i] == ')') {\n            $level--;\n            if ($level < 0) {\n                return false; // Unbalanced ')'\n            }\n        }\n    }\n    return $level == 0; // Unbalanced '(' if level != 0\n}