class MyClass {\n    private $myVariable;\n\n    public function __construct($variable) {\n        $this->myVariable = $variable;\n    }\n\n    public function myFunction() {\n        echo $this->myVariable;\n    }\n}\n\n// Initialize the class and pass a variable\n$myClass = new MyClass('Hello World');\n\n// Call a function that uses the passed variable\n$myClass->myFunction(); // Outputs: Hello World