require_once 'vendor/autoload.php';\n\nuse PsdPhp\PsdFile;\nuse PsdPhp\Layer;\n\n// Load the PSD file\n$psd = new PsdFile('path/to/file.psd');\n\n// Get the layers\n$layers = $psd->getLayers();\n\n// Loop through each layer and create an image\nforeach ($layers as $layer) {\n    $image = imagecreatetruecolor($layer->getWidth(), $layer->getHeight());\n    $layer->render($image);\n    imagepng($image, 'layer-' . $layer->getName() . '.png');\n    imagedestroy($image);\n}