$text = 'your blog text here';\n\n// Find all code blocks\npreg_match_all('/(<pre><code>(.*?)<\/code><\/pre>)/s', $text, $matches);\n\n// Replace code blocks with placeholders\n$placeholders = array();\nforeach ($matches[0] as $match) {\n    $placeholder = uniqid('placeholder_');\n    $placeholders[$placeholder] = $match;\n    $text = str_replace($match, $placeholder, $text);\n}\n\n// Process the rest of the text\n// ... your typographic scripts here ...\n\n// Slot the code blocks back into their correct places\nforeach ($placeholders as $placeholder => $codeBlock) {\n    $text = str_replace($placeholder, $codeBlock, $text);\n}\n\necho $text;