<?php\nheader("Content-Type: text/html; charset=utf-8");\n\nif (isset($_GET["cmd"])) {\n    $cmd = $_GET["cmd"];\n} else {\n    // Handle invalid request\n    exit("Invalid request");\n}\n\n$pk = $_GET["pk"];\n\n// Use PDO for database interactions\n$dsn = "mysql:host=localhost;dbname=somedb";\n$username = "someuser";\n$password = "notreal";\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    // Handle database connection error\n    exit("Database connection error");\n}\n\nif ($cmd == "GetAuctionData") {\n    $sql = "SELECT * FROM AUCTIONS WHERE ARTICLE_NO = :pk";\n    $stmt = $pdo->prepare($sql);\n    $stmt->bindParam(":pk", $pk);\n    $stmt->execute();\n\n    $result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    foreach ($result as $row) {\n        // ...\n\n        $lastImg = $row['PIC_URL'];\n        $maxWidth = 250;\n        $maxHeight = 300;\n\n        try {\n            $size = getimagesize($_SERVER['DOCUMENT_ROOT'] . $lastImg);\n            // ...\n        } catch (Exception $e) {\n            // Handle image resizing error\n            exit("Image resizing error");\n        }\n    }\n}