<?php\nob_start(); // Start output buffering\n\n// Generate your XML here\n?>\n<xml>\n    <morexml>\n        <?php while(){ ?>\n        <somegeneratedxml>\n        <?php } ?>\n    </morexml>\n    <lastofthexml>\n<?php\n$xml = ob_get_contents(); // Capture the generated XML into a variable\nob_end_clean(); // End output buffering and clean the buffer\n\n// Now you can use the $xml variable to print the XML in both places\n?>\n\n<html>\n    <pre>\n        <?php echo htmlspecialchars($xml); // Print the XML for the user to preview ?>\n    </pre>\n    <form>\n        <input id="xml" value="<?php echo htmlspecialchars($xml); // Print the XML again as the form value ?>"/>\n    </form>\n</html>