if ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $day = filter_var($_POST['day'], FILTER_VALIDATE_INT, ['options' => ['min_range' => 1, 'max_range' => 31]]);\n    $month = filter_var($_POST['month'], FILTER_VALIDATE_INT, ['options' => ['min_range' => 1, 'max_range' => 12]]);\n    $year = filter_var($_POST['year'], FILTER_VALIDATE_INT, ['options' => ['min_range' => 1900, 'max_range' => date('Y')]]);\n\n    if ($day && $month && $year) {\n        // Additional check for valid date (e.g., no Feb 30)\n        if (checkdate($month, $day, $year)) {\n            // Date is valid, proceed to insert into MySQL\n            $dob = sprintf("%04d-%02d-%02d", $year, $month, $day);\n            // Insert into MySQL\n            insertDOBIntoMySQL($dob);\n        } else {\n            echo "Invalid date.";\n        }\n    } else {\n        echo "Invalid input.";\n    }\n}\n\nfunction insertDOBIntoMySQL($dob)\n{\n    // Assuming you have a PDO connection\n    $dsn = 'mysql:host=localhost;dbname=yourdatabase';\n    $username = 'yourusername';\n    $password = 'yourpassword';\n\n    try {\n        $pdo = new PDO($dsn, $username, $password);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    } catch (PDOException $e) {\n        echo 'Connection failed: ' . $e->getMessage();\n        exit();\n    }\n\n    try {\n        $sql = 'INSERT INTO yourtable (dob) VALUES (:dob)';\n        $stmt = $pdo->prepare($sql);\n        $stmt->execute(['dob' => $dob]);\n        echo "DOB inserted successfully.";\n    } catch (PDOException $e) {\n        echo 'Insertion failed: ' . $e->getMessage();\n    }\n}