<?php\ninclude 'myconnection.php';\n\n// Delete existing data\n$mysqli = new mysqli($db_host, $db_username, $db_password, $db_name);\n$mysqli->query("DELETE FROM mytable");\n\n// Load XML file using XMLReader\n$xml_reader = new XMLReader();\n$xml_reader->open('cachy/copy.xml');\n\n// Prepare INSERT statement\n$stmt = $mysqli->prepare('\n    INSERT INTO mytable (\n        onsaledate,\n        onsaletime,\n        eventdate,\n        eventtime,\n        buyat_short_deeplink_url,\n        product_name,\n        level1,\n        level2,\n        VenueName,\n        VenueDMAID\n    ) VALUES (\n        ?,\n        ?,\n        ?,\n        ?,\n        ?,\n        ?,\n        ?,\n        ?,\n        ?,\n        ?\n    )\n');\n\n// Loop through XML elements\nwhile ($xml_reader->read() && $xml_reader->name === 'product') {\n    $product_name = trim($xml_reader->getAttribute('product_name'));\n\n    // Bind parameters\n    $stmt->bind_param(\n        'ssssssssss',\n        $xml_reader->getAttribute('OnsaleDate'),\n        $xml_reader->getAttribute('OnsaleTime'),\n        $xml_reader->getAttribute('EventDate'),\n        $xml_reader->getAttribute('EventTime'),\n        $xml_reader->getAttribute('buyat_short_deeplink_url'),\n        $product_name,\n        $xml_reader->getAttribute('level1'),\n        $xml_reader->getAttribute('level2'),\n        $xml_reader->getAttribute('VenueName'),\n        $xml_reader->getAttribute('VenueDMAID')\n    );\n\n    // Execute prepared statement\n    $stmt->execute();\n}\n\n// Close XML reader and MySQL connection\n$xml_reader->close();\n$mysqli->close();\n\necho 'Records inserted successfully!';