$conn = oci_connect('username', 'password', 'host/service_name');\nif (!$conn) {\n    $e = oci_error();\n    trigger_error(htmlentities($e['message']), E_USER_ERROR);\n}\n\n$sql = "INSERT INTO my_gtt (id, value) VALUES (1, 'test')";\n$stid = oci_parse($conn, $sql);\noci_execute($stid);\n\n// do some work...\n\n// when script exits, data in GTT will be automatically deleted\noci_free_statement($stid);\noci_close($conn);