// Facebook API Configuration\n$fb_app_id = 'YOUR_APP_ID';\n$fb_app_secret = 'YOUR_APP_SECRET';\n$fb_access_token = 'YOUR_ACCESS_TOKEN';\n\n// WordPress REST API Configuration\n$wp_rest_api_url = 'https://your-wordpress-site.com/wp-json/wp/v2/comments';\n\n// Fetch Facebook Comments\n$fb_comments_url = 'https://graph.facebook.com/v13.0/' . $fb_app_id . '/comments?access_token=' . $fb_access_token;\n$fb_comments_response = json_decode(file_get_contents($fb_comments_url), true);\n\n// Create WordPress Comment\nforeach ($fb_comments_response['data'] as $fb_comment) {\n  $wp_comment_data = array(\n    'post' => $fb_comment['post_id'],\n    'author_name' => $fb_comment['from']['name'],\n    'author_url' => $fb_comment['from']['link'],\n    'content' => $fb_comment['message'],\n  );\n  $wp_comment_response = wp_remote_post($wp_rest_api_url, array(\n    'method' => 'POST',\n    'headers' => array('Content-Type' => 'application/json'),\n    'body' => json_encode($wp_comment_data),\n  ));\n}