function add() {\n    if (!empty($this->data)) {\n        $this->Post->set($this->data);\n        if ($this->Post->validates()) {\n            if ($this->Post->save()) {\n                $this->Session->setFlash('Your post has been saved.');\n                $this->redirect(array('action' => 'index'));\n            }\n        } else {\n            $this->Session->setFlash('Validation failed.');\n        }\n    }\n}