public function getCategorieenAsArray(){\n    $catList = array();\n    $query = "SELECT DISTINCT datasource_id, title FROM table";\n    if ($rs=C_DB::fetchRecordset($query)) {\n        while ($row=C_DB::fetchRow($rs)) {\n            if(!empty($row["title"])){\n                $catList[$row["datasource_id"]] = $row["title"];\n            }\n        }\n    }\n    return $catList;\n}