<?php\n\nnamespace Deployer;\n\nrequire 'recipe/common.php';\n\n// Project details\nset('application', 'my_app');\nset('repository', 'git@github.com:username/my_app.git');\n\n// Deployment environments\nhost('staging')\n    ->set('hostname', 'staging.example.com')\n    ->set('username', 'username')\n    ->set('deploy_path', '/var/www/staging');\n\nhost('production')\n    ->set('hostname', 'example.com')\n    ->set('username', 'username')\n    ->set('deploy_path', '/var/www/production');\n\n// Deployment tasks\ntask('deploy', [\n    'deploy:info',\n    'deploy:prepare',\n    'deploy:lock',\n    'deploy:release',\n    'deploy:update_code',\n    'deploy:shared',\n    'deploy:writable',\n    'deploy:clear_paths',\n    'deploy:symlink',\n    'deploy:unlock',\n    'cleanup',\n]);