// MovieDataAccess.php\nclass MovieDataAccess {\n    public function getMovieData($movieId) {\n        $query = "SELECT * FROM movies WHERE id = ?";\n        $stmt = $this->db->prepare($query);\n        $stmt->bind_param("i", $movieId);\n        $stmt->execute();\n        $result = $stmt->get_result();\n        return $result->fetch_assoc();\n    }\n\n    public function getMovieGenres($movieId) {\n        $query = "SELECT g.name FROM genres g JOIN movie_genres mg ON g.id = mg.genre_id WHERE mg.movie_id = ?";\n        $stmt = $this->db->prepare($query);\n        $stmt->bind_param("i", $movieId);\n        $stmt->execute();\n        $result = $stmt->get_result();\n        return $result->fetch_all(MYSQLI_ASSOC);\n    }\n}\n\n// MoviePage.php\nrequire_once "MovieDataAccess.php";\n\n$movieId = 123;\n$movieDataAccess = new MovieDataAccess();\n$movieData = $movieDataAccess->getMovieData($movieId);\n$movieGenres = $movieDataAccess->getMovieGenres($movieId);\n\n// Use $movieData and $movieGenres to display the movie informatio