function topPg() {\n    $p = $this->retrieve("id = '$this->parent'");\n    if ($p->parent != 0) {\n        return $this->topPgRecursive($p);\n    } else {\n        return $p;\n    }\n}\n\nfunction topPgRecursive($page) {\n    $p = $this->retrieve("id = '$page->parent'");\n    if ($p->parent != 0) {\n        return $this->topPgRecursive($p);\n    } else {\n        return $p;\n    }\n}