// User Aggregate\nclass User {\n    private $id;\n    private $name;\n    private $posts;\n    // getters and setters\n}\n\n// Post Aggregate\nclass Post {\n    private $id;\n    private $content;\n    private $comments;\n    // getters and setters\n}\n\n// UserRepository\ninterface UserRepository {\n    public function findById($id);\n    public function save(User $user);\n    public function delete($id);\n}\n\n// PostRepository\ninterface PostRepository {\n    public function findById($id);\n    public function save(Post $post);\n    public function delete($id);\n}