// Scenario 1: Same folder for all customers\n$folder = '/path/to/shared/files';\nfor ($i = 0; $i < 500; $i++) {\n    // Access files from shared folder\n    include $folder . '/file.php';\n}\n\n// Scenario 2: Separate folders for each customer\nfor ($i = 0; $i < 500; $i++) {\n    $folder = '/path/to/customer/' . $i . '/files';\n    // Access files from customer-specific folder\n    include $folder . '/file.php';\n}