$formdesc = $_POST['formdesc'];\n\n// Convert to UTF-8 encoding if necessary\n$formdesc = iconv(mb_detect_encoding($formdesc, mb_detect_order(), true), "UTF-8", $formdesc);\n\n// Use htmlspecialchars() instead of htmlentities() for UTF-8\n$formdesc = htmlspecialchars($formdesc, ENT_QUOTES, 'UTF-8');\n\n// Then, to protect against SQL injection, use prepared statements\n// This example uses PDO\n$pdo = new PDO('mysql:host=your_host;dbname=your_db;charset=UTF8');\n$stmt = $pdo->prepare("INSERT INTO your_table (description) VALUES (:description)");\n$stmt->bindParam(':description', $formdesc);\n$stmt->execute();