<?php\n$db = pg_connect("host=localhost port=5432 dbname=lang user=password=") or die(":(");\npg_set_client_encoding($db, "UTF-8");\n\n$lan = $_REQUEST['lan'];\n\nif (array_key_exists('sub', $_REQUEST)) {\n    $sql = "INSERT INTO table1 (japan) VALUES($1)";\n    $result = pg_prepare($db, 'insert_japan', $sql);\n    $result = pg_execute($db, 'insert_japan', array($lan));\n    if (!$result) {\n        die("Error: " . pg_last_error($db));\n    }\n}\n?>\n\n<html>\n<body>\n  <form action="" method="post">\n    <input type="text" name="lan" />\n    <input type="submit" name="sub" />\n  </form>\n</body>\n</html>