$apiKey = 'YOUR_API_KEY';\n$baseCurrency = 'AUD';\n$currencies = 'USD,EUR';\n\n$url = 'https://api.exchangerate-api.com/v4/latest/' . $baseCurrency . '?access_key=' . $apiKey . '&symbols=' . $currencies;\n\n$response = json_decode(file_get_contents($url), true);\n\nif ($response['success']) {\n    $exchangeRates = $response['rates'];\n\n    // Print exchange rates\n    foreach ($exchangeRates as $currency => $rate) {\n        echo $baseCurrency . ' to ' . $currency . ': ' . $rate . "\n";\n    }\n} else {\n    echo 'Failed to retrieve exchange rates.';\n}