class Search {\n    private static $instance;\n\n    private function __construct() {}\n\n    public static function getInstance() {\n        if (!self::$instance) {\n            self::$instance = new self();\n        }\n        return self::$instance;\n    }\n\n    public function doSomething() {\n        // do something\n    }\n}\n\n$search1 = Search::getInstance();\n$search2 = Search::getInstance();\n\nvar_dump($search1 === $search2); // bool(true)\n\n// but if you run this script again in a separate request\n// $search3 will be a new instance, not the same as $search1\n$search3 = Search::getInstance();