$this->load->library('image_lib');\n\n// Load the frame image\n$config['image_library'] = 'gd2';\n$config['source_image'] = './path/to/frame/image.jpg';\n$config['new_image'] = './path/to/output/image.jpg';\n\n$this->image_lib->initialize($config);\n\n// Load the uploaded image\n$config['source_image'] = './path/to/uploaded/image.jpg';\n$config['new_image'] = './path/to/output/image.jpg';\n$config['overlay_path'] = './path/to/frame/image.jpg'; // Path to the frame image\n$config['overlay_alignment'] = 'center'; // Align the frame to the center of the uploaded image\n$config['overlay_opacity'] = 1; // Set the opacity to 1 (100%)\n\n$this->image_lib->overlay($config);\n\n// Clear the config and initialize the image lib again\n$this->image_lib->clear();\n$this->image_lib->initialize($config);\n\n// Save the merged image\n$this->image_lib->resize();