class ArrayAccessor {\n    private $array;\n\n    public function __construct($array) {\n        $this->array = $array;\n    }\n\n    public function getvalue($key) {\n        return $this->array[$key];\n    }\n}\n\n// usage\n$arr = array(\n    'row1' => array('item' => 'value1'),\n    'row2' => array('item' => 'value2'),\n);\n\n// set the internal pointer to the first element\nreset($arr);\n\n// get the value\n$accessor = new ArrayAccessor(current($arr));\necho $accessor->getvalue('item'); // outputs: value1