// Save a session variable\nfunction mymodule_save_session($key, $value) {\n  $session = &drupal_static('session');\n  $session[$key] = $value;\n  session_save_session($session);\n}\n\n// Load a session variable\nfunction mymodule_load_session($key) {\n  $session = &drupal_static('session');\n  session_load_session($session);\n  return isset($session[$key]) ? $session[$key] : NULL;\n}