function generateDropdown($locations, $parent_id = 0, $indent = '') {\n  $html = '';\n  foreach ($locations as $location) {\n    if ($location['parentid'] == $parent_id) {\n      $html .= '<option value="' . $location['locationid'] . '">' . $indent . $location['name'] . '</option>';\n      $html .= generateDropdown($locations, $location['locationid'], $indent . '--');\n    }\n  }\n  return $html;\n}\n\n// example usage:\n$locations = array(\n  array('locationid' => 1, 'name' => 'Location 1', 'parentid' => 0),\n  array('locationid' => 2, 'name' => 'Sublocation 1', 'parentid' => 1),\n  array('locationid' => 3, 'name' => 'Sublocation 2', 'parentid' => 1),\n  array('locationid' => 4, 'name' => 'Location 2', 'parentid' => 0),\n  array('locationid' => 5, 'name' => 'Sublocation 4', 'parentid' => 4),\n  // ...\n);\n\n$dropdown_html = generateDropdown($locations);\necho '<select>' . $dropdown_html . '</select>';