function levenshteinDistance($str1, $str2) {\n    $m = array();\n    $n = strlen($str1);\n    $o = strlen($str2);\n\n    for ($i = 0; $i <= $n; $i++) {\n        $m[$i][0] = $i;\n    }\n\n    for ($j = 0; $j <= $o; $j++) {\n        $m[0][$j] = $j;\n    }\n\n    for ($i = 1; $i <= $n; $i++) {\n        for ($j = 1; $j <= $o; $j++) {\n            $cost = ($str1[$i - 1] == $str2[$j - 1]) ? 0 : 1;\n            $m[$i][$j] = min($m[$i - 1][$j] + 1, $m[$i][$j - 1] + 1, $m[$i - 1][$j - 1] + $cost);\n        }\n    }\n\n    return $m[$n][$o];\n}\n\n$distance = levenshteinDistance($string1, $string2);\necho "Levenshtein Distance: $distance\n";