$page_id = 'your_page_id_here';\n$ip_address = $_SERVER['REMOTE_ADDR'];\n\n$query = "SELECT * FROM ip_blocks WHERE page_id = '$page_id' AND ip_address = '$ip_address'";\n$result = mysql_query($query);\n\nif (mysql_num_rows($result) == 0) {\n    // IP address not found, increment hit counter\n    $query = "UPDATE hits SET hits = hits + 1 WHERE id = '$page_id' AND date = CURDATE()";\n    mysql_query($query);\n\n    // Add IP address to database\n    $query = "INSERT INTO ip_blocks (page_id, ip_address) VALUES ('$page_id', '$ip_address')";\n    mysql_query($query);\n}