$string = "12,15,22";\n$ids = explode(',', $string); // Convert string to array of IDs\n\n// Create placeholders for the IDs (e.g., ?,?,?)\n$placeholders = implode(',', array_fill(0, count($ids), '?'));\n\n// Prepare the SQL statement\n$sql = "SELECT usrFirst, usrLast FROM tblusers WHERE usrID IN ($placeholders)";\n\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute($ids); // Bind and execute the query with the IDs\n\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    foreach ($results as $row) {\n        echo $row['usrFirst'] . ' ' . $row['usrLast'] . "\n";\n    }\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}