function getFirstSentence($text) {\n    $sentenceEnders = array('.', '!', '?');\n    $shortestPos = strlen($text);\n    foreach ($sentenceEnders as $ender) {\n        $pos = strpos($text, $ender);\n        if ($pos !== false && $pos < $shortestPos) {\n            $shortestPos = $pos;\n        }\n    }\n    return substr($text, 0, $shortestPos + 1);\n}\n\n$text = "We prefer questions that can be answered, not just discussed.  Provide details. Write clearly and simply.";\necho getFirstSentence($text);