function getmails() {\n    $stmt = $mysqli->prepare("SELECT subscribe_email FROM subscribes ORDER BY subscribe_id DESC");\n    $stmt->execute();\n    $emails = array();\n    while ($row = $stmt->fetch_assoc()) {\n        $emails[] = $row['subscribe_email'];\n    }\n    return implode(', ', $emails);\n}\n\nfunction announce() {\n    if (isset($_POST['announce'])) {\n        $toEmail = getmails();\n        $emailSubject = $_POST['title'];\n        $mailheader = "From: newsletter@subdomain.domain.com\r\n";\n        $mailheader .= "Reply-to: ".$_POST['author']."@subdomain.domain.com\r\n";\n        $mailheader .= "Content-type: text/html; charset=iso-8859-1\r\n";\n        $messageBody = "Author: ".$_POST['author']."<br>";\n        $messageBody .= "Newsletter: ".$_POST['content']."<br>";\n        mail($toEmail, $emailSubject, $messageBody, $mailheader) or die("Failure");\n    }\n}