function pass_by_value($array) {\n    // do nothing\n}\n\nfunction pass_by_reference(&$array) {\n    // do nothing\n}\n\n$array = range(1, 100000);\n\n$start = microtime(true);\nfor ($i = 0; $i < 1000; $i++) {\n    pass_by_value($array);\n}\necho "Pass by value: " . (microtime(true) - $start) . "\n";\n\n$start = microtime(true);\nfor ($i = 0; $i < 1000; $i++) {\n    pass_by_reference($array);\n}\necho "Pass by reference: " . (microtime(true) - $start) . "\n";