$page_id = 'your_page_id_here';\n$cookie_name = 'visited_pages';\n\nif (!isset($_COOKIE[$cookie_name])) {\n    // Set cookie with page ID\n    $cookie_value = serialize(array($page_id));\n    setcookie($cookie_name, $cookie_value, time() + 86400); // expires in 1 day\n\n    // Increment hit counter\n    $query = "UPDATE hits SET hits = hits + 1 WHERE id = '$page_id' AND date = CURDATE()";\n    mysql_query($query);\n} elseif (!in_array($page_id, unserialize($_COOKIE[$cookie_name]))) {\n    // Add page ID to existing cookie\n    $cookie_value = unserialize($_COOKIE[$cookie_name]);\n    $cookie_value[] = $page_id;\n    setcookie($cookie_name, serialize($cookie_value), time() + 86400); // expires in 1 day\n\n    // Increment hit counter\n    $query = "UPDATE hits SET hits = hits + 1 WHERE id = '$page_id' AND date = CURDATE()";\n    mysql_query($query);\n}