function cmp($a, $b) {\n    $now = new DateTime();\n    $a_date = new DateTime($a);\n    $b_date = new DateTime($b);\n\n    // If the date has passed this year, use next year's date for comparison\n    if ($a_date < $now) {\n        $a_date->modify('+1 year');\n    }\n    if ($b_date < $now) {\n        $b_date->modify('+1 year');\n    }\n\n    return $a_date->getTimestamp() - $b_date->getTimestamp();\n}\n\n$a = array("1980-04-14", "2007-06-08", "2008-12-25", "1978-11-03");\nusort($a, 'cmp');\nprint_r($a);