// app/Http/Middleware/LogRequest.php\nnamespace App\Http\Middleware;\n\nuse Closure;\n\nclass LogRequest\n{\n    public function handle($request, Closure $next)\n    {\n        // log request\n        $log = 'requests.log';\n        file_put_contents($log, date('Y-m-d H:i:s') . ' ' . $request->method() . ' ' . $request->url() . PHP_EOL, FILE_APPEND);\n        \n        $response = $next($request);\n        \n        // log response\n        $log = 'responses.log';\n        file_put_contents($log, date('Y-m-d H:i:s') . ' ' . $response->getStatusCode() . PHP_EOL, FILE_APPEND);\n        \n        return $response;\n    }\n}