// application/configs/application.ini\n[production]\nphpSettings.display_startup_errors = 0\nphpSettings.display_errors = 0\nincludePaths.library = APPLICATION_PATH "/../library"\nbootstrap.path = APPLICATION_PATH "/Bootstrap.php"\nbootstrap.class = "Bootstrap"\n\n[modules]\nfrontend.path = APPLICATION_PATH "/modules/frontend"\nadmin.path = APPLICATION_PATH "/modules/admin"\n\n// application/Bootstrap.php\nclass Bootstrap extends Zend_Application_Bootstrap_Bootstrap\n{\n    protected function _initFrontend()\n    {\n        $this->bootstrap('frontend');\n        $this->getResource('frontend');\n    }\n\n    protected function _initAdmin()\n    {\n        $this->bootstrap('admin');\n        $this->getResource('admin');\n    }\n}