$db = Zend_Db::factory('Pdo_Mysql', array(\n    'host'     => 'localhost',\n    'username' => 'username',\n    'password' => 'password',\n    'dbname'   => 'dbname'\n));\n\n// Enable buffered queries for MySQL\n$db->getConnection()->setAttribute(PDO::MYSQL_ATTR_USE_BUFFERED_QUERY, true);\n\n// Start transaction\n$db->beginTransaction();\n\ntry {\n    // Call stored procedures\n    $db->query('CALL stored_procedure_1()');\n    $db->query('CALL stored_procedure_2()');\n\n    // Commit transaction\n    $db->commit();\n} catch (Exception $e) {\n    // Rollback transaction on error\n    $db->rollBack();\n    throw $e;\n}