function flattenArray($array, $pattern) {\n    $result = [];\n    foreach ($array as $key => $value) {\n        if (preg_match($pattern, $key)) {\n            if (is_array($value)) {\n                $result = array_merge($result, flattenArray($value, '/.*/'));\n            } else {\n                $result[] = $value;\n            }\n        } elseif (is_array($value)) {\n            $result = array_merge($result, flattenArray($value, $pattern));\n        }\n    }\n    return $result;\n}\n\n// Example usage:\n$catalogue = [\n    'Artist1' => [\n        'Album1' => ['Song1', 'Song2'],\n        'Album2' => ['Song3', 'Song4'],\n    ],\n    'Artist2' => [\n        'Album3' => ['Song5', 'Song6'],\n        'Album4' => ['Song7', 'Song8'],\n    ],\n    'Sunshine Artist' => [\n        'Album5' => ['Song9', 'Song10'],\n    ],\n    'Artist3' => [\n        'Sunny Album' => ['Song11', 'Song12'],\n    ],\n];\n\n$pattern = '/sun/i'; // Case-insensitive search for 'sun'\n$result = flattenArray($catalogue, $pattern);\n\nprint_r($result);