$dsn = 'mysql:host=localhost;dbname=database';\n$username = 'username';\n$password = 'password';\n\n$pdo = new PDO($dsn, $username, $password);\n\n$sp_query = "DELIMITER // CREATE PROCEDURE my_procedure() BEGIN SELECT * FROM my_table; END // DELIMITER ;";\n\n$pdo->exec($sp_query);\n\nif ($pdo->errorCode() === '00000') {\n    echo "Stored procedure created successfully";\n} else {\n    echo "Error creating stored procedure: (" . $pdo->errorCode() . ") " . $pdo->errorInfo()[2];\n}\n\n$pdo = null;