// Frontend application\n// logout.php\n\n<?php\n// ...\n\n// Send request to backend application to destroy session\n$ch = curl_init('https://backend-app.com/session_destroy.php');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, array('session_id' => $_SESSION['session_id']));\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// ...