function getAndConvertFavicon($domain) {\n    // Example: $domain = "example.domain.tld";\n    $faviconUrl = "http://" . $domain . "/favicon.ico";\n    $outputPath = "/favicons/" . $domain . ".png";\n\n    // Fetch the favicon\n    $curl = curl_init($faviconUrl);\n    curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\n    $faviconData = curl_exec($curl);\n    $httpCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);\n    curl_close($curl);\n\n    // Check if the favicon exists (HTTP 200)\n    if ($httpCode != 200) {\n        echo "Favicon not found or could not be retrieved.\n";\n        return;\n    }\n\n    // Save the favicon temporarily\n    $tmpPath = "/tmp/" . $domain . "_favicon.ico";\n    file_put_contents($tmpPath, $faviconData);\n\n    // Convert to PNG if necessary\n    $imageInfo = getimagesize($tmpPath);\n    switch ($imageInfo['mime']) {\n        case 'image/png':\n            // Already PNG, just move it\n            rename($tmpPath, $outputPath);\n            break;\n        case 'image/x-icon':\n        case 'image/vnd.microsoft.icon':\n            // Convert .ico to PNG\n            $img = imagecreatefromico($tmpPath);\n            imagepng($img, $outputPath);\n            imagedestroy($img);\n            unlink($tmpPath); // Remove the temporary file\n            break;\n        default:\n            echo "Unsupported favicon type.\n";\n    }\n}\n\n// Usage\ngetAndConvertFavicon("example.domain.tld");