use Zend\Db\TableGateway\TableGateway;\n\n// Create a table gateway for the installation state table\n$tableGateway = new TableGateway('installation_state', $dbAdapter);\n\n// Check if the application has been installed\n$installationState = $tableGateway->select(['installed' => true]);\n\nif ($installationState->count() == 0) {\n    // Redirect to installation setup page\n    return $this->redirect()->toRoute('install');\n}