<?php\n\nfunction gaussianDistribution($mean, $stdDev, $x) {\n    $gaussian = (1 / ($stdDev * sqrt(2 * M_PI))) * exp(-pow($x - $mean, 2) / (2 * pow($stdDev, 2)));\n    return $gaussian;\n}\n\n$mean = 10; // peak at 10 o'clock\n$stdDev = 2; // adjust this to spread out the distribution\n$hours = range(0, 23); // 24 hours\n\n$points = [];\nforeach ($hours as $hour) {\n    $point = gaussianDistribution($mean, $stdDev, $hour);\n    $points[] = [$hour, $point];\n}\n\nprint_r($points);\n\n?>