$image = imagecreatefromgif('image.gif');\n\n// Allocate new colors\n$black = imagecolorallocate($image, 0, 0, 0);\n$lightBlue = imagecolorallocate($image, 173, 216, 230);\n\n// Swap white with black\nimagefill($image, 0, 0, $black);\n\n// Loop through each pixel and replace the graph line color with light blue\nfor ($x = 0; $x < imagesx($image); $x++) {\n    for ($y = 0; $y < imagesy($image); $y++) {\n        $colorIndex = imagecolorat($image, $x, $y);\n        $color = imagecolorsforindex($image, $colorIndex);\n        if ($color['red'] == 255 && $color['green'] == 0 && $color['blue'] == 0) { // assuming the graph line color is red\n            imagesetpixel($image, $x, $y, $lightBlue);\n        }\n    }\n}\n\nheader('Content-Type: image/gif');\nimagegif($image);\nimagedestroy($image);