$imageUrl = 'https://example.com/image.jpg';\n$imageSize = '800x600'; // desired output size\n\n// Use GD library to resize the image\n$image = imagecreatefromjpeg($imageUrl);\n$image = imagescale($image, $imageSize);\n\n// Output the resized image as a base64-encoded string\nob_start();\nimagejpeg($image);\n$base64Image = base64_encode(ob_get_contents());\nob_end_clean();\n\necho '<img src="data:image/jpeg;base64,' . $base64Image . '">';