$html = file_get_contents('newspaper.html'); // load the HTML content\n$doc = new DOMDocument();\n$doc->loadHTML($html);\n$xpath = new DOMXPath($doc);\n\n$image = $xpath->query("//div[@class='fondoprincipal']//img/@src")->item(0)->nodeValue;\n$title = $xpath->query("//a[@class='cabezaprincesto']/text()")->item(0)->nodeValue;\n$description = $xpath->query("//div[@class='fondoprincipal']//div[@class='resumenesto']/a/text()")->item(0)->nodeValue;\n\n// create a new HTML page with the parsed values\n$newHtml = '<html><body>';\n$newHtml .= '<img src="' . $image . '">';\n$newHtml .= '<h1>' . $title . '</h1>';\n$newHtml .= '<p>' . $description . '</p>';\n$newHtml .= '</body></html>';\n\nfile_put_contents('newpage.html', $newHtml);